/**
 * @author liuyaowei488
 * @date created in 2019-9-4 20:19
 */
public class ReadDirectory {
    public static void main(String[] args) {

        System.out.println(Runtime.getRuntime().maxMemory());
        FileOutputStream fos = null;
        try {
            String path = "D:\\workspace\\lyw\\omsdoc.7z";
            File file = new File(path);
            byte[] code = getBytefromFile(file);
//
//            String encodeBase64 =  Base64.encodeBase64String(code);
//            System.out.println(encodeBase64);
//            byte[] decodeBase64 = Base64.decodeBase64("");

            fos = new FileOutputStream("D:\\workspace\\lywZip\\lywSelf.7z");
            fos.write(code);
            fos.flush();
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            IOUtil.close(fos);
        }

    }

    public static byte[] getBytefromFile(File file) throws IOException {
        byte[] bytes = null;
        if (file == null) {
            return bytes;
        }
        InputStream is = null;
        try {
            int length = (int) file.length();
            if (length > Integer.MAX_VALUE) {
                return bytes;
            }
            is = new FileInputStream(file);
            bytes = new byte[length];
            int offset = 0;
            int numRead = 0;

            while (offset < bytes.length && (numRead = is.read(bytes, offset, bytes.length - offset)) >= 0) {
                offset += numRead;
            }
        } catch (Exception e) {
        } finally {
            IOUtil.close(is);
        }
        return bytes;
    }
}
